(function() {
  var Hash, Seq, fs;
  fs = require('fs');
  Hash = require('hashish');
  Seq = require('seq');
  Seq().seq_(function(next) {
    return fs.readdir(__dirname, next);
  }).flatten().parEach_(function(next, file) {
    return fs.stat(__dirname + '/' + file, next.into(file));
  }).seq_(function(next) {
    var sizes;
    sizes = Hash.map(next.vars, function(s) {
      return s.size;
    });
    return console.dir(sizes);
  });
}).call(this);
