(function() {
  this.title = 'Log In';
  h1(this.title);
  p("A local var: " + a_local_var);
  p("A context var: " + this.a_context_var);
  form({
    action: '/',
    method: 'post'
  }, function() {
    div({
      "class": 'field'
    }, function() {
      label({
        "for": 'username'
      }, function() {
        return 'Username: ';
      });
      return input({
        id: 'username',
        name: 'username'
      });
    });
    return div({
      "class": 'field'
    }, function() {
      label({
        "for": 'password'
      }, function() {
        return 'Password: ';
      });
      return input({
        id: 'password',
        name: 'password'
      });
    });
  });
}).call(this);
