(function() {
  var coffeekup, meryl;
  meryl = require('meryl');
  coffeekup = require('coffeekup');
  meryl.h('GET /', function(req, resp) {
    var people;
    people = ['bob', 'alice', 'meryl'];
    return resp.render('layout', {
      content: 'index',
      context: {
        people: people
      }
    });
  });
  meryl.run({
    templateDir: 'templates',
    templateExt: '.coffee',
    templateFunc: coffeekup.adapters.meryl
  });
  console.log('Listening on 3000...');
}).call(this);
